				 <p className="p1">You try again to talk with the store owner--this is illegal, but you donâ€™t want to get him in trouble</p>
                <Link to="/Simulation1/Option1">
                <button
				type="button"
                class='btn2 btn-light'
                >
				Choice one
                </button>
                </Link>
				</div>

        </div>
      </nav>
    </>
  );
}
export default NavBar;
